$(window).on('load', function () {
    if ($(".preloader")) {
        preLoader();
    } else {}
})

function preLoader() {
    const preLoader = document.querySelector(".preloader");
    if (!preLoader) return;
    let counts = setInterval(updated, 4);
    let upto = 0;

    function updated() {
        let count = document.querySelector(".preloader .counter span");
        count.innerHTML = ++upto;
        if (upto === 100) {
            clearInterval(counts);
            animateLoader();
        }
    }

    function animateLoader() {
        const tl = gsap.timeline({
            duration: 0.1,
        });

        tl.to(".preloader h5", {
                autoAlpha: 0,
            })
            .to(".preloader .counter", {
                autoAlpha: 0,
            })
            .to(".preloader .line", {
                width: '100vw',
            })
            .to(".preloader .line", {
                xPercent: 100,
            })
            .to(".preloader .bar", {
                height: 0,
                stagger: 0.09,
                ease: "power4.inOut",
            })
            .to(".preloader", {
                css: {
                    display: "none",
                },
            })
            .from(".header-main__logo > h3", {
                y: -50,
                autoAlpha: 0,
            }, "show")
            .from(".header-main__nav > .nav-item", {
                autoAlpha: 0,
                y: -50,
                stagger: 0.05,
            })
            .from(".main-banner", {
                autoAlpha: 0,
                duration: 1,
            }, "show")
            .from(".main-banner__content", {
                autoAlpha: 0,
                yPercent: 100,
                stagger: 0.05,
            }, "show")            
            .from(".main-banner__img", {
                autoAlpha: 0,
                xPercent: 100,
            }, "show")
            .from(".bannercardSlider .swiper-wrapper .swiper-slide", {
                autoAlpha: 0,
                yPercent: 100,
                stagger: 0.05,
            }, "show");        
    }
}

function lenisSetup() {
	const lenis = new Lenis({
		duration: 1.5,
		easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
		smooth: true,
		mouseMultiplier: 1,
	});

	function raf(time) {
		lenis.raf(time);
		requestAnimationFrame(raf);
	}
	requestAnimationFrame(raf);
	document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
		anchor.addEventListener("click", function (e) {
			e.preventDefault();
			lenis.scrollTo(this.getAttribute("href"));
		});
	});
	lenis.scrollTo("top");
}

function replaceHash() {
	document.querySelectorAll("a").forEach((a) => {
		let href = a.getAttribute("href");
		a.href = href ?
			href.startsWith("#") && href.endsWith("#") ?
			href.replace("#", "javascript:void(0)") :
			href :
			"javascript:void(0)";
	});
}

